import Login from './Login'
import React, { useState } from 'react';

const Register = () =>{ 
    const [registerPage, setRegisterPage] = useState(true);


    const [username,setUsername]=useState('');
    const [first_name, setFirstName] = useState('');
    const [last_name, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmation, setConfirmation] = useState('');


    const registerPageToLoginPage = (e) =>{
        e.preventDefault();
        setRegisterPage(false);
    }

    const usernameFormHandler = (e) =>{
        setUsername(e.target.value);
    }
    const passwordFormHandler = (e) =>{
        setPassword(e.target.value);
    }
    const confirmationFormHandler = (e) =>{
        setConfirmation(e.target.value);
    }
    const emailFormHandler = (e) =>{
        setEmail(e.target.value);
    }
    const firstNameFormHandler = (e) =>{
        setFirstName(e.target.value);
    }
    const lastNameFormHandler = (e) =>{
        setLastName(e.target.value);
    }

    const loginPageOrRegisterPage = () => {
        if (registerPage) {
            return (
                <div>
                    <form >
                        <input type="text" placeholder="Username" value={username} onChange={usernameFormHandler}/>
                        <input type="text" placeholder="First Name" value={first_name} onChange={firstNameFormHandler}/>
                        <input type="text" placeholder="Last Name" value={last_name} onChange={lastNameFormHandler}/>
                        <input type="text" placeholder="Email" value={email} onChange={emailFormHandler}/>
                        <input type="password" placeholder="Password" value={password} onChange={passwordFormHandler}/>
                        <input type="password" placeholder="Confirm Password" value={confirmation} onChange={confirmationFormHandler}/>
                        <input type="submit" value="Sign Up" />
                    </form>
                    <p>
                       Already have an account? <a href='' onClick={registerPageToLoginPage}>Login</a>
                    </p>
                </div>
            );
        } else {
            return <Login />;
        }
    };


    const submitHandler = (e) =>{
        
    }


    return(
        <div>
            {loginPageOrRegisterPage()}
  
        </div>
    )

}
export default Register;
