import React, { useState, useEffect } from 'react';


const User= (props) =>{

    useEffect(()=>{
        fetchUserPosts();
    },)
    const [userPosts, setUserPosts] = useState([]);

    const fetchUserPosts = async () => {
        const data = await fetch(
            `http://127.0.0.1:8000/api/user_specific_posts/${props.username}`
        );

        const post = await data.json();
     
        setUserPosts(post)
        //  console.log(post);
    };
    console.log(props.userData)
    return(
        <div>
            <p>Username: {props.userData.username}</p>
            <p>{props.userData.first_name} {props.userData.last_name}</p>
            <h3>Followers: {props.userData.followers.length}</h3>
            <h3>Following: {props.userData.following.length}</h3>
        </div>

    )
}

export default User;
