import React, { useState, useEffect } from 'react';

const Post = (props) =>{

   // console.log(props.post)
    var post_created_year = parseInt(props.post.created_at.substring(0,4));
    var post_created_month = parseInt(props.post.created_at.substring(5,7));
    var post_created_day = parseInt(props.post.created_at.substring(8,10));
    var post_created_hour = parseInt(props.post.created_at.substring(11,13));
    var post_created_minute = parseInt(props.post.created_at.substring(14,16));
    // post_created_year,post_created_month,post_created_day,post_created_hour,post_created_minute
    const getDate = () =>{
        var post_created = new Date(props.post.created_at);
        console.log(today.getFullYear())
        // var year_difference = today.getFullYear()-post_created_year;
        // var month_difference = today.getMonth()+1 - post_created_month;
        // var day_difference = today.getDate() - post_created_day;
        // console.log(month_difference)


    }
    

    const [liked,setLiked] = useState(props.post.liked);
    const [likes, setLikes] = useState(props.post.likes)
 

   const getCookie = (name) =>{
        var cookieValue = null;
        if (document.cookie && document.cookie !== '') {
            var cookies = document.cookie.split(';');
            for (var i = 0; i < cookies.length; i++) {
                var cookie = cookies[i].trim();
                // Does this cookie string begin with the name we want?
                if (cookie.substring(0, name.length + 1) === (name + '=')) {
                    cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                    break;
                }
            }
        }
        return cookieValue;
    }


    const likeHandler = (e) =>{
        e.preventDefault();
        var csrftoken = getCookie('csrftoken')
        var url = `http://127.0.0.1:8000/api/post/${props.post.id}/${props.userId}/like`
        fetch(url,{
            'method':"POST",
            headers:{
                'Content-type':'application/json',
                'X-CSRFToken':csrftoken,
            },
            body:JSON.stringify({'like':true})

        }).then( data => data.json())
        .then(
            data => {
                console.log(data)
                if(data==="Liked successfully"){
                    setLiked(true);
                    setLikes(likes=>likes+1)
               
                }

            }
          ).catch(function(error){
            console.log('ERROR:', error)
          })


    }

    const unlikeHandler = (e) =>{
        e.preventDefault();
        var csrftoken = getCookie('csrftoken')
        var url = `http://127.0.0.1:8000/api/post/${props.post.id}/${props.userId}/like`
        fetch(url,{
            'method':"POST",
            headers:{
                'Content-type':'application/json',
                'X-CSRFToken':csrftoken,
            },
            body:JSON.stringify({'like':false})

        }).then(data=>data.json())
        .then(
            data =>{
                console.log(data)
                if(data==="Unliked successfully"){
                    setLiked(false)
                    setLikes(likes => likes-1)
           
                }
            }
        ).catch(error =>{
            console.log("ERROR",error)
        })


    }


    return(
        <div>
            <hr/>
            <h3>@{props.post.username}</h3>
            <p>{props.post.content}</p>
            <p><b>Likes:</b> {likes}</p>
            {liked   ? <button onClick={unlikeHandler}> Unlike </button> : <button onClick={likeHandler}> Like </button>}

            {getDate()}
            
            <hr/>
        </div>
    )
}
export default Post;
