import React, { useState, useEffect } from 'react';



const Login = () =>{
    const [username,setUsername] = useState('');
    const [password,setPassword] = useState('');
    const usernameFormHandler = (e) =>{
        setUsername(e.target.value)
     
    }
    const passwordFormHandler = (e) =>{
        setPassword(e.target.value)
    }
    const submitHandler = () =>{
        var csrftoken = this.getCookie('csrftoken')
        fetch('http://127.0.0.1:8000/api/login', {
            method:'POST',
            headers:{
              'Content-type':'application/json',
              'X-CSRFToken':csrftoken,
            },
            body
          }).then((response)  => {
              this.fetchTasks()
              this.setState({
                 activeItem:{
                id:null, 
                title:'',
                completed:false,
              }
              })
          }).catch(function(error){
            console.log('ERROR:', error)
          })
      
    }

    return(
        <div>
            <form>
                <input type="text" placeholder="Username" onChange={usernameFormHandler} value={username}/>
                <input type="text" placeholder="Password" onChange={passwordFormHandler} value={password}/>
                <input type="button" value="login" /> 
            </form>
        </div>
    )
}
export default Login;
