import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Nav from './components/Nav';
import Login from './components/Login';
import Main from './components/Main'



function App() {


    useEffect(() => {
        getIsLogged();
    }, []);


    const [posts, setPosts] = useState([]);
    const [notifications, setNotifications]=useState([]);

    const [username, setUsername] = useState('');
    const [userId,setUserId] = useState('');
    const [isLogged, setIsLogged] = useState('');


    useEffect(() => {
        fetchPosts();
        fetchNotifications();
        saveIsLogged();
    }, [isLogged]);

    const saveIsLogged = () => {
        localStorage.setItem('isLogged', isLogged);
        localStorage.setItem('username', username);
    };

    const getIsLogged = () => {
        if (localStorage.getItem('isLogged') === null) {
            localStorage.setItem('isLogged', "false");
            localStorage.setItem('username', '');
            localStorage.setItem('userId','')

        } else {
            setIsLogged(localStorage.getItem('isLogged'));
            setUsername(localStorage.getItem('username'))
            setUserId(localStorage.getItem('userId'))
        }
    };

    const fetchPosts = async () => {
        const data = await fetch(
            `http://127.0.0.1:8000/api/following_posts/${username}`
        );

        const post = await data.json();
        setPosts(post);

      //  console.log(post);
    };


    const fetchNotifications = async () => {
        const data = await fetch(
            `http://127.0.0.1:8000/api/notifications/${username}`
        );

        const notification = await data.json();
        setNotifications(notification);


    };


    return (


            <div className='App'>
                <Main isLogged={isLogged} posts={posts} username={username} setUsername={setUsername} setIsLogged={setIsLogged} notifications={notifications}/>
            </div>

    );
}

export default App;
