import React, { useState, useEffect } from 'react';
import Post from './Post';
import Comment from './Comment';
import CreateComment from './CreateComment';

const User = (props) => {
    const getCookie = (name) => {
        var cookieValue = null;
        if (document.cookie && document.cookie !== '') {
            var cookies = document.cookie.split(';');
            for (var i = 0; i < cookies.length; i++) {
                var cookie = cookies[i].trim();
                // Does this cookie string begin with the name we want?
                if (cookie.substring(0, name.length + 1) === name + '=') {
                    cookieValue = decodeURIComponent(
                        cookie.substring(name.length + 1)
                    );
                    break;
                }
            }
        }
        return cookieValue;
    };

    useEffect(()=>{
        fetchUserData();
    },[])
    useEffect(() => {
        fetchUserPosts();
    }, [props.username,props.commentSubmit]);
    const [userPosts, setUserPosts] = useState([]);
    const [followers,setFollowers]= useState('');
    const [following,setFollowing] = useState('');
    const[userData,setUserData] = useState([]);

    const fetchUserPosts = async () => {
        const data = await fetch(
            `http://127.0.0.1:8000/api/user_specific_posts/${props.username}`
        );

        const post = await data.json();

        setUserPosts(post);
        //console.log(post);
    };

    const fetchUserData = async () => {

        const data = await fetch(
            `http://127.0.0.1:8000/api/user/${props.username}/${props.userId}`
        );

        const user_data = await data.json();
        console.log(user_data)
        setFollowers(user_data.followers.length);
        setFollowing(user_data.following.length);
        setUserData(user_data);
      
    };


    const likeButtonHandler = (e) =>{
        e.preventDefault();
        var csrftoken = getCookie('csrftoken');
        //`http://127.0.0.1:8000/api/${userData.username}/${props.userId};

    }

    const followButton = () =>{
        if(userData.can_follow){
            if(userData.already_following){
                return(
                    <button>Unfollow</button>
                )
            }else{
                return(
                    <button>Follow</button>
                )
            }
        }
    }









    return (
        <div>
           

            <p>
                {userData.first_name} {userData.last_name}
            </p>
            <p>@{userData.username}</p>

            {followButton()}

            <h3>Followers: {followers}</h3>
            <h3>Following: {following}</h3>
            {userPosts.map((post) => {
                return (
                    <div>
                        <Post post={post} key={post.id} userId={props.userId} />

                        
                    </div>
                );
            })}
        </div>
    );
};

export default User;
